// import React from 'react';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import MovieSearch from './Components/MovieSearch/MovieSearch';
import MovieList from './Components/MultipleActorsInMovie/MovieList';
import Actors2000And2015 from './Components/Actors2000And2015/Actors2000And2015';
import MovieRatings from './Components/MovieRatings/MovieRatings';
import UpdateRating from './Components/UpdateRatings/UpdateRating';
import CopyRight from './Components/CopyRight/CopyRight';

const BASE_URL = 'http://localhost:3001';

const App = () => {
  const [director, setDirector] = useState('');
  const [movies, setMovies] = useState([]);
  const [actors, setActors] = useState([]);
  const [moviesByJames, setMoviesByJames] = useState([]);
  const [error, setError] = useState(null);
  const [actorsByYear, setActorsByYear] = useState([]);

  useEffect(() => {
    // Fetch actors who acted in a movie before 2000 and also in a movie after 2015
    axios
      .get(`${BASE_URL}/actors/2000-2015`)
      .then((response) => setActorsByYear(response.data))
      .catch((error) => setError(error.response.data));
  }, []);


  useEffect(() => {
    // Fetch movies with multiple actors
    axios
      .get(`${BASE_URL}/movies/multiple-actors`)
      .then((response) => setActors(response.data))
      .catch((error) => setError(error.response.data));
  }, []);

  const getMoviesByJames = () => {
    axios
      .get(`${BASE_URL}/movies/james_cameron`)
      .then((response) => {
        setMoviesByJames(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  const handleDirectorChange = (event) => {
    setDirector(event.target.value);
  };

  const handleSearch = () => {
    axios
      .get(`http://localhost:3001/movies/director?name=${director}`)
      .then((response) => {
        setMovies(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <div className="App">
      <>
        <CopyRight />
        {/* MovieByJamesCameron */}
        <div className="main">
          <div className="movie_by_james">
            <h1>Movies Directed by james_cameron</h1>
            {moviesByJames.length > 0 ? (
              <ul>
                {moviesByJames.map((movie) => (
                  <li key={movie.Mov_id}>{movie.Mov_Title}</li>
                ))}
              </ul>
            ) : (
              <p>Click Below To Get Movies</p>
            )}
            <button className="button" onClick={getMoviesByJames}>
              getMoviesByJames
            </button>
          </div>

          <div className="movieSearch">
            <h1>Movie Search</h1>
            <div className="movieSearchContent">
              <input
                type="text"
                className="user"
                placeholder="Search By Director Name"
                required={true}
                value={director}
                onChange={handleDirectorChange}
              />

              <button className="button" onClick={handleSearch}>
                Search
              </button>
            </div>
            <ul>
              {movies.map((movie) => (
                <li key={movie.Mov_Title}>{movie.Mov_Title}</li>
              ))}
            </ul>
          </div>
        </div>

        {/* Movies with MultipleActors */}
        <div className="MovieListMain">
          <div className="MovieList">
            <h1>Movies with Multiple Actors</h1>
            {actors.length > 0 ? (
              <ul>
                {actors.map((actor) => (
                  <li key={actor.Mov_id}>{actor.Mov_Title}</li>
                ))}
              </ul>
            ) : (
              <p>No movies found.</p>
            )}
          </div>
          <div className="MovieList2"></div>
        </div>

        {/* <Actors2000And2015 /> */}

        <MovieRatings />
        <UpdateRating />
      </>
    </div>
  );
};
export default App;
